import { SubscribeType } from '../core-api/stage-subscription';
import { StageParticipantInfo } from './stage-participant/stage-participant';
import { LocalStageStream } from './stage-stream';
/**
 * A StageStrategy dictates certain behavior on the [[Stage]]
 */
export interface StageStrategy {
    /**
     * Should return the local streams that should be published to the stage.
     * This will be invoked during the [[Stage.join]] call and every subsequent [[Stage.refreshStrategy]] call.
     * This will not be invoked if [[StageStrategy.shouldPublishParticipant]] returns false
     */
    stageStreamsToPublish(): LocalStageStream[];
    /**
     * Should return true if you wish to publish to the [[Stage]] otherwise return false.
     */
    shouldPublishParticipant(participant: StageParticipantInfo): boolean;
    /**
     * Should return whether or not you wish to subscribe to a given participant. Return [[SubscribeType.AUDIO_VIDEO]]
     * to subscribe to audio and video. Return [[SubscribeType.AUDIO_ONLY]] to subscribe to just audio. Return [[SubscribeType.NONE]]
     * to not subscribe at all. This will be invoked after a new participant joins or against every participant
     * when [[Stage.refreshStrategy]] is called
     */
    shouldSubscribeToParticipant(participant: StageParticipantInfo): SubscribeType;
}
